CREATE TABLE IF NOT EXISTS `pdf_subsections` (
  `id_subsection` int(11) NOT NULL AUTO_INCREMENT,
  `id_document` int(11) NOT NULL DEFAULT '0',
  `id_section` int(11) NOT NULL DEFAULT '0',
  `title_subsection` varchar(250) NOT NULL DEFAULT '',
  `centrato` tinyint(1) NOT NULL DEFAULT '0',
  `sub_title` varchar(250) NOT NULL,
  `description` text NOT NULL,
  `order_num` smallint(6) NOT NULL DEFAULT '0',
  PRIMARY KEY (`id_subsection`),
  KEY `order_num` (`order_num`),
  KEY `id_document` (`id_document`),
  KEY `id_section` (`id_section`),
  KEY `order_num_2` (`order_num`)
) ENGINE=MyISAM AUTO_INCREMENT=7 DEFAULT CHARSET=utf8mb4;

